        const result = await connection.query("UPDATE movimientos SET fecha_ingreso='" + fecha_ingreso + "',descrip_ingreso='" + descrip_ingreso + "',monto_ingreso=" + monto_ingreso + ",fecha_egreso='" + fecha_egreso + "',descrip_egreso='" + descrip_egreso + "',monto_egreso=" + monto_egreso + " WHERE id_movimiento=" + id + ";")
        res.json("updateMove")
    } catch (error) {
        res.status(500)
        res.send(error.message)
    }
}

const BorrarMovimiento = async (req, res) => {
    try {
        const { id } = req.params;
        const connection = await getConnection();
        const result = await connection.query("DELETE from movimientos where id_movimiento="+id+"");
        res.json("DatoBorrado");
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};
export const methods = {
    getMovimientos,
    AddMovimientos,
    ActualizarMovimientos,
    BorrarMovimiento,
    ObtenerMovimiento
}

